import React from 'react';
import './sportsEquipment.css';


class SportsEquipment extends React.Component {
  constructor(props){
    super(props);
    
    this.state = {
          displayMenu: false, inputStudent:'', inputCharge:''
        };
   
     this.showDropdownMenu = this.showDropdownMenu.bind(this);
     this.hideDropdownMenu = this.hideDropdownMenu.bind(this);
     this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
   
   };
   handleChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const StudentCode = target.name;
    
    this.setState({
      [StudentCode]: value,
      
    });
  }

  handleSubmit(event) {
    alert('A code was submitted: ' + this.state.inputStudent);
    alert('A code was submitted: ' + this.state.inputStudent);
    event.preventDefault();
  }

   showDropdownMenu(event) {
       event.preventDefault();
       this.setState({ displayMenu: true }, () => {
       document.addEventListener('click', this.hideDropdownMenu);
       });
     }
   
     hideDropdownMenu() {
       this.setState({ displayMenu: false }, () => {
         document.removeEventListener('click', this.hideDropdownMenu);
       });
    
     }
    
    
    
   
     render() {
       return (
         <div className="pContainer"> 
          <h1 className="pTitle">Prestamo de Equipo Deportivo</h1>
           <div  className="dropdown" style = {{background:"red",width:"200px"}} >
            <div className="button" onClick={this.showDropdownMenu}>  <a id="default">Select</a> </div>
   
             { this.state.displayMenu ? (
             <ul>
            <li><a id="element1">{this.props.object[0].SportsElement}</a></li>
            <li><a id="element2">{this.props.object[0].SportsElement1}</a></li>
            <li><a id="element3">{this.props.object[0].SportsElement2}</a></li>
           
             </ul>
           ):
           (
             null
           )
           }
   
          </div>
          <form onSubmit={this.handleSubmit}>
           <div className="Peoplecontainer">

              <p className="inputStudentLabel" >Codigo Estudiante: </p>
              <input type="text" name="inputStudent" value={this.state.inputStudent} onChange={this.handleChange} className="inStudentCode"></input>
              <p className="inputChargeLabel">Codigo Encargado: </p>
              <input type="text" name= "inputCharge" value={this.state.inputStudent}  onChange={this.handleChange} className="inChargeCode"></input>
              <button type="submit" value="Submit" className="Borrow">Prestar</button>
              <div className="Log">
                <h2 className="Logheader">Transacciones</h2>
                <p className="tLogElement">Elemento: </p>
                <p className="tLogStudentCode">Codigo Estudiante: </p>
                <p className="tLogChargeCode">Codigo Encargado:  </p>
              </div>
           </div>
           </form>
          
            
          </div>
   
       );
     }
}

export default SportsEquipment;
